import{_ as a,c as t,o as r,aN as o}from"./chunks/framework.B3J4P44d.js";const n="/docs/imgs/system-bios.jpg",_=JSON.parse('{"title":"虚拟机 日记","description":"","frontmatter":{"title":"虚拟机 日记","date":"2020-01-26T00:00:00.000Z","permalink":"/service/vmware","tags":["Linux"],"categories":["Linux"],"private":true,"privateRealm":"common"},"headers":[],"relativePath":"02.服务端/vmware-undone.md","filePath":"02.服务端/vmware-undone.md","lastUpdated":1761308264000}'),i={name:"02.服务端/vmware-undone.md"};function s(d,e,c,p,l,m){return r(),t("div",null,e[0]||(e[0]=[o('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /docs/02.服务端/vmware-undone.md for this page in Markdown format</div><h1 id="虚拟机-日记" tabindex="-1">虚拟机 日记 <a class="header-anchor" href="#虚拟机-日记" aria-label="Permalink to &quot;虚拟机 日记&quot;">​</a></h1><h2 id="系统启动" tabindex="-1">系统启动 <a class="header-anchor" href="#系统启动" aria-label="Permalink to &quot;系统启动&quot;">​</a></h2><p>计算机通电后，第一件事情就是读取输入 ROM 芯片的开机程序，这个程序叫做基本输入输出系统 <code>BIOS</code>（Basic Input/Output System）</p><p><code>BIOS</code> 程序首先检查计算机硬件是否满足运行的基本条件，这个叫做硬件自检（Power-On Self-Test）</p><p>如果硬件出现问题，主板会发出不同含义的蜂鸣，启动中止，如果没有问题，屏幕会显示出 CPU、内存、硬盘等信息</p><p>硬件自检完成后，BIOS 会把控制权交给下一阶段的启动程序；这时候 BIOS 要知道下一阶段的启动程序具体存放在哪一个设备，BIOS 需要有一个外部存储设备的排序，排在前面的设备就是优先转交控制权的设备，这种排序叫做启动顺序（Boot Sequence）BIOS 按照启动顺序，把控制权交给排在第一位的存储设备</p><p>记得当时还是 xp 的系统的时候装系统的时候流行这么一句话，开机就按 delete，然后再找小 boot，进去之后把第一启动设备改成 CDROM 或者 USB，使用光盘或者 u 盘来装系统</p><p align="center" class="p-images"><img src="'+n+'" height="" title="第一启动设备"></p><h2 id="镜像下载" tabindex="-1">镜像下载 <a class="header-anchor" href="#镜像下载" aria-label="Permalink to &quot;镜像下载&quot;">​</a></h2><p><a href="http://mirrors.aliyun.com/centos/7/isos/x86_64/" target="_blank" rel="noreferrer">centos7</a></p>',11)]))}const h=a(i,[["render",s]]);export{_ as __pageData,h as default};
